//code for "Show All" and "Hide All" buttons

function showAll() {

  for(var x = 1; x < 11; x++) {
    document.querySelector("#a" + x).style.visibility = "visible";
  }

}
    
function hideAll() {

  for(var x = 1; x < 11; x++) {
    document.querySelector("#a" + x).style.visibility = "hidden";
  }
}