//code for first question

function show1() {

    var answer = document.querySelector('#a1');
    
    answer.style.visibility = "visible";
  
}
  
function hide1() {
    
    var answer = document.querySelector('#a1');
    
    answer.style.visibility = "hidden";
  
}

